function generate_calendar($year, $month, $days = array(), $day_name_length = 3, $month_href = NULL, $first_day = 0, $pn = array(), $setLocal){
  
  # set LC_TIME to the local date-formats 
  if( $setLocal ){
    setlocale (LC_TIME, $setLocal);
  }
  
	$first_of_month = gmmktime(0,0,0,$month,1,$year);
