	$day_names = array(); #generate all the day names according to the current locale
	for($n=0,$t=(3+$first_day)*86400; $n<7; $n++,$t+=86400) #January 4, 1970 was a Sunday
		$day_names[$n] = ucfirst(gmstrftime(\'%A\',$t)); #%A means full textual day name

	list($month, $year, $month_name, $weekday) = explode(\',\',gmstrftime(\'%m,%Y,%B,%w\',$first_of_month));
	$weekday = ($weekday + 7 - $first_day) % 7; #adjust for $first_day
	$title   = htmlentities(ucfirst($month_name)).\'&nbsp;\'.$year;  #note that some locales don\'t capitalize month and day names

	#Begin calendar. Uses a real <caption>. See http://diveintomark.org/archives/2002/07/03
	@list($p, $pl) = each($pn); @list($n, $nl) = each($pn); #previous and next links, if applicable
	if($p) $p = \'<span class="calendar-prev">\'.($pl ? \'<a href="\'.$pl.\'" id="calendar-prev">\'.$p.\'</a>\' : $p).\'</span>&nbsp;\';
	if($n) $n = \'&nbsp;<span class="calendar-next">\'.($nl ? \'<a href="\'.$nl.\'" id="calendar-next">\'.$n.\'</a>\' : $n).\'</span>\';
	$calendar = \'<table class="calendar" cellspacing="0" cellpadding="0">\'."\\n".
		\'<caption class="calendar-month">\'.$p.($month_href ? \'<a href="\'.htmlspecialchars($month_href).\'">\'.$title.\'</a>\' : $title).$n."</caption>\\n<tr>";

	if($day_name_length){ #if the day names should be shown ($day_name_length > 0)
		#if day_name_length is >3, the full name of the day will be printed
		foreach($day_names as $d)
			$calendar .= \'<th abbr="\'.htmlentities($d).\'">\'.htmlentities($day_name_length < 4 ? substr($d,0,$day_name_length) : $d).\'</th>\';
		$calendar .= "</tr>\\n<tr>";
	}

	if($weekday > 0) $calendar .= \'<td colspan="\'.$weekday.\'">&nbsp;</td>\'; #initial \'empty\' days
	for($day=1,$days_in_month=gmdate(\'t\',$first_of_month); $day<=$days_in_month; $day++,$weekday++){
		if($weekday == 7){
			$weekday   = 0; #start a new week
			$calendar .= "</tr>\\n<tr>";
		}
		if(isset($days[$day]) and is_array($days[$day])){
			@list($link, $classes, $title, $content) = $days[$day];
			if(is_null($content))  $content  = $day;
			$calendar .= \'<td\'.($classes ? \' class="\'.htmlspecialchars($classes).\'">\' : \'>\').
				($link ? \'<a href="\'.htmlspecialchars($link).\'" title="\'.htmlspecialchars($title).\'">\'.$content.\'</a>\' : $content).\'</td>\';
		}
		else $calendar .= "<td>$day</td>";
	}
	if($weekday != 7) $calendar .= \'<td colspan="\'.(7-$weekday).\'">&nbsp;</td>\'; #remaining "empty" days

	return $calendar."</tr>\\n</table>\\n";
}


