	"[0-9]{2}|[1-9]{1}[0-9]{1}|[0-9])|localhost|([a-zA-Z0-9\\-]+\\.)*[a-zA-Z0-9\\-]+\\.(com|edu|gov|int|mil|net|org|biz|arpa|info|name|pro|aero|coop|museum|" .
	"[a-zA-Z]{2}))(\\:[0-9]+)*(/($|[a-zA-Z0-9\\.\\,\\?\\\'\\\\\\+&%\\$#\\=~_\\-]+))*$"; // validates a URL

	$autolinkValue = array (); //-- stores the link values
	$autolinkTitle = array (); //-- stores the title attribute values
	$autolinkDefault = array (); //-- will store the abbreviation/acronym value

	//-- setup db table name
	$autolinkTable = $modx->getFullTableName(\'autolinkwords\');

	//-- get keywords from db
	$rs = $modx->db->select(\'*\', $autolinkTable);

	//if no keywords in table, then exit now
	if ($modx->db->getRecordCount($rs) == 0) {
		return $string;
	}

	while ($row = $modx->db->getRow($rs)) {
		$temp_word = $row[\'keyword\'];
		$autolinkValue[$row[\'keyword\']] = $row[\'link\'];
		$autolinkTitle[$row[\'keyword\']] = $row[\'title\'];
		$autolinkDefault[$row[\'keyword\']] = $row[\'default_type\'];
	}

	//-- get the body tags
	preg_match(\'/(<body.*?>)/\', $string, $open);
	preg_match(\'/(<\\/body.*?>)/\', $string, $close);
	$body_open = $open[1];
	$body_close = $close[1];

	unset ($open);
	unset ($close);

