			$match_noencode = "/\\b" . preg_quote($keyword, "/") . "\\b/i"; //-- check for non-encoded match

			switch ($link) {
				//-- autolink template functions
				case function_exists("autolink_" . $link) :
					$autolink_function = "autolink_" . $link;
					$substitute = $autolink_function ($keyword);
					break;
					//-- highlight
				case (eregi("$valid_html_color", $link) ? $link : !$link) :
					$substitute = \'<span class="autolink_highlight" title="\' . $title . \'" style="background-color:\' . $link . \';">\' . htmlentities($keyword) . \'</span>\';
					break;
					//-- just link
				case (eregi("$valid_url_regexp", $link) ? $link : !$link) :
					$substitute = \'<a class="autolink_link" href="\' . $link . \'" title="\' . $title . \'">\' . htmlentities($keyword) . \'</a>\';
					break;
					//-- Acronym or just alt-text
				default :
					//-- be smart about whether a title has been passed or not
					$title = ($title <> \'\') ? $title : $link;
					if ($default == 0) {
						$substitute = \'<abbr title="\' . $title . \'"><span class="autolink_abbr" title="\' . $title . \'">\' . htmlentities($keyword) . \'</span></abbr>\';
