								if ($match_replace == 1) {
									$substitute = preg_replace(\'/\\b\' . $keyword . \'\\b/\', $kmatch[0], $substitute);
								}
								//-- replace keyword with the substitute
								$token = preg_replace($match_noencode, $substitute, $token);
								$must_tokenize = TRUE; //-- re-tokenize next time around
							}
						}

					} else //-- this token is markup
						{
						if (preg_match("#<\\s*a\\s+[^>]*>#i", $token)) { //-- found <a ...>
