						}
						elseif (preg_match("#<($blocked_tags)>#i", $token)) { //-- found excluded tags
							$tag_level++;
						}
						elseif (preg_match("#<\\/($blocked_tags)>#i", $token)) { //-- found excluded closing tags
							$tag_level--;
						}
					}
					$filtered .= $token; //-- this token has now been filtered
				}
			}
			$text = $filtered; //-- filtering completed for this link
		}
		//-- add section to the output
		$out .= $text;
	}
	//-- return the compiled output to the browser
	return $head . $body_open . trim($out) . $body_close . $footer;
}

